styles:
.form-container {
  background: linear-gradient(
    to right bottom,
    rgba(255, 255, 255, 0.8),
    rgba(255, 255, 255, 0.3)
  );
  border-radius: 0.5rem;
  z-index: 2;
  backdrop-filter: blur(2rem);
  display: flex;
  justify-content: space-between;
  padding: 2rem;
  width: 30rem;
  box-shadow: 6px 6px 20px rgba(122, 122, 122, 0.212);
}
.form-container .card-title {
  width: 30%;
}
.form-container form {
  width: 70%;
  padding: 0rem 0 2.3rem 1rem;
}
.form-container form label {
  font-size: 1.2rem;
  margin-bottom: 0.2rem;
}
.form-container form .field-container {
  width: 100%;
  margin-bottom: 1rem;
}
.form-container form .field-container .input-field {
  display: block;
  width: 97%;
  border-radius: 0.5rem;
  height: 3rem;
  margin: 0.3rem 0;
  font-size: 1.1rem;
  padding-left: 0.5rem;
  border: 1px solid #d7d7d7;
  outline: none;
}
.form-container form .field-container .error {
  border: 1px solid #f00;
}
.form-container form .field-container .error-message {
  color: #f00;
}
.form-container form .btn-container {
  display: flex;
  justify-content: flex-end;
  height: 2rem;
}
.form-container form .btn-container button {
  color: #fff;
  text-align: center;
  border: none;
  border-radius: 0.5rem;
  min-width: 10rem;
  height: 3rem;
  font-size: 1.2rem;
  margin-top: 1rem;
}
.form-container form .btn-container button {
  background: linear-gradient(to right top, #000000e8, #00000061);
  outline: none;
  cursor: pointer;
}
.form-container form .btn-container button[disabled] {
  background: #b4b4b4;
  cursor: not-allowed;
}
@media screen and (max-width: "625px"), (max-width: "425px") {
  .form-container {
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 15rem;
  }
  .form-container h1 {
    margin: 0 0 2rem 0;
  }
  .form-container form {
    width: 100%;
  }
  .form-container button {
    width: 100%;
  }
}
